import { SwipeableDrawer } from '@mui/material';
import React from 'react';
import { BlogDetail } from '../App';
import '../App.css'

const Write = () => {
  const { logged, setLogged, blogs, setBlogs, write, setWrite } = BlogDetail();
  const posting = ()=>{
    var img = document.querySelector('#image').files[0];
    img = (img === undefined ? "https://thumbs.dreamstime.com/b/no-image-available-icon-photo-camera-flat-vector-illustration-132483141.jpg" : URL.createObjectURL(img));
    const title = document.querySelector("#title").value;
    const body = document.querySelector("#cotent").value;
    const authorName = logged;
    const postID = Math.floor( blogs.length + Math.random()*100)

  }
  return (
    <SwipeableDrawer anchor={'bottom'} open={write} PaperProps={{ sx: { height: "70vh" } }} onClose={() => { setWrite(false) }} onOpen={() => { setWrite(true) }}>
      <div id="writeContainer">
        <h1> Write Your Blog </h1>
        <input type={'file'} id="image" />
        <input type={'text'} placeholder="Title" autoFocus id='title' />
        <textarea placeholder='Content' id='content' />
        <button onClick={posting}> Post </button>
      </div>
    </SwipeableDrawer>
  )
}

export default Write
