import './App.css';
import { BrowserRouter as Routers, Routes, Route } from 'react-router-dom';
import Home from './Components/Home';
import Login from './Components/Login';
import Register from './Components/Register';
import Write from './Components/Write';
import Post from './Components/Post';
import Error from './Components/Error';
import Navbar from './Components/Navbar';
import { createContext, useContext, useState } from 'react';
import data from './Components/Assets/data.json'

const user = createContext();
function App() {
  var [logged, setlogged] = useState('Shanky');
  const [blogs, setBlogs] = useState(data);
  const [write,setWrite] = useState(false);

  return (
    <>
      <user.Provider value={{ logged, setlogged, blogs, setBlogs,write,setWrite }} >
        <div id='container'>
          <Routers>
            <Navbar />
            <Routes>
              <Route exact path='/' element={<Home />} />
              <Route exact path='/login' element={<Login />} />
              <Route exact path='/register' element={<Register />} />
              <Route exact path='/ediyt' element={<Write />} />
              <Route exact path='/post/:postID' element={<Post />} />
              <Route exact path='/post/:*' element={<Error />} />
            </Routes>
          </Routers>

        </div>
      </user.Provider>
    </>
  );
}

export default App;

export const BlogDetail = () => {
  return useContext(user)
}
