import { Avatar, Card, CardActions, CardContent, CardHeader, CardMedia, IconButton, Typography } from '@mui/material'
import React from 'react'
import FavoriteIcon from '@mui/icons-material/Favorite';
import ShareIcon from '@mui/icons-material/Share';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import { BlogDetail } from '../App';
const BlogCard = ({ val }) => {
    const {logged,blogs,setBlogs} = BlogDetail();

    const del = () => {
       setBlogs([...blogs,blogs.filter])
    }
    const edit = () => {

    }
    return (
        <Card sx={{ maxWidth: 500 }}>
            <CardHeader
                avatar={
                    <Avatar >
                        {val.authorName.charAt(0)}
                    </Avatar>
                }
                title={val.authorName}
                action={
                    (logged) && <><IconButton aria-label="Edit" onClick={edit}>
                        <EditIcon sx={{ color: "black" }} />
                    </IconButton><IconButton aria-label="Delete" onClick={del} >
                            <DeleteIcon sx={{ color: "red" }} />
                        </IconButton></>
                }
            />
            <CardMedia
                component="img"
                height="194"
                image={val.src}
                alt="Paella dish"
            />
            <CardContent>
                <Typography variant="body1" color="text.primary">
                    {val.title}
                </Typography>
            </CardContent>
            <CardActions disableSpacing>
                <IconButton aria-label="add to favorites">
                    <FavoriteIcon />
                </IconButton>
                <IconButton aria-label="share">
                    <ShareIcon />
                </IconButton>
            </CardActions>

        </Card>
    )
}

export default BlogCard
